import React, { useEffect, useState } from 'react'
import './Home.scss'
import axios from 'axios';
import { useDispatch, useSelector } from 'react-redux';
import { GetContact } from '../../api/GetContact';
import { ContactDetailModelType, ContactModelType } from '../../models/ContactModel';
import { withStyles, Theme, createStyles, makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const Home = () => {
    const [ contactList, setContactList ] = useState([] as ContactDetailModelType[])
    const [ isToggleMenu, setIsToggleMenu ] = useState(false);


    useEffect(()=>{
        getContact()
    },[])

    const getContact = () => {
        GetContact().then((result) => {
            setContactList(result)
            console.log(result, 'apani')
        })
    }

    const toggleMenu = () => {
        setIsToggleMenu(!isToggleMenu);
    }


    //Table Styling
    const StyledTableCell = withStyles((theme: Theme) =>
        createStyles({
            head: {
            backgroundColor: theme.palette.common.black,
            color: theme.palette.common.white,
            },
            body: {
            fontSize: 14,
            },
        }),
    )(TableCell);

    const StyledTableRow = withStyles((theme: Theme) =>
        createStyles({
            root: {
            '&:nth-of-type(odd)': {
                backgroundColor: theme.palette.action.hover,
            },
            },
        }),
    )(TableRow);

    const useStyles = makeStyles({
        table: {
          minWidth: 700,
        },
      });

      const classes = useStyles();
    return (
        <div className="contact-home">
            <div className={`menu-bar ${isToggleMenu ? 'show' : ''}`} onClick={toggleMenu}>
                <div className="menu-item">
                    a
                </div>
                <div className="menu-item">
                    a
                </div>
                <div className="menu-item">
                    a
                </div>
                <div className="menu-item">
                    a
                </div>
            </div>
            <div className="contact-main">
                <div className="header">
                    Selamat datang
                </div>
                <TableContainer component={Paper} style={{padding: '50px'}}>
                    <Table className={classes.table} aria-label="customized table">
                        <TableHead>
                        <TableRow>
                            <StyledTableCell>Dessert (100g serving)</StyledTableCell>
                            <StyledTableCell align="right">Calories</StyledTableCell>
                            <StyledTableCell align="right">Fat&nbsp;(g)</StyledTableCell>
                            <StyledTableCell align="right">Carbs&nbsp;(g)</StyledTableCell>
                            <StyledTableCell align="right">Protein&nbsp;(g)</StyledTableCell>
                        </TableRow>
                        </TableHead>
                        <TableBody>
                        {contactList.map((res) => (
                            <StyledTableRow key={res.id}>
                            <StyledTableCell component="th" scope="row">
                                {res.firstName}
                            </StyledTableCell>
                            <StyledTableCell align="right">
                                <img src={res.photo} alt="photo" style={{width: '50px', height: '50px'}} />
                            </StyledTableCell>
                            <StyledTableCell align="right">{res.firstName}</StyledTableCell>
                            <StyledTableCell align="right">{res.lastName}</StyledTableCell>
                            <StyledTableCell align="right">{res.age}</StyledTableCell>
                            </StyledTableRow>
                        ))}
                        </TableBody>
                    </Table>
                </TableContainer>
            </div>

            {
                contactList.map((res) => {
                    return (
                        <div className="contact-list">
                            {/* {res.age} */}
                        </div>
                    )
                })
            }
        </div>
    )
}

export default Home;
