import React, { useEffect, useState } from 'react'
import './EditContact.scss'
import axios from 'axios';
import { useDispatch, useSelector } from 'react-redux';
import { GetContact } from '../../api/GetContact';
import { ContactDetailModelType, ContactModelType } from '../../models/ContactModel';
import { withStyles, Theme, createStyles, makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import { defaultAvatar } from '../../helpers/defaultAvatar';
import { useHistory, useParams } from 'react-router';
import { GetEditContact } from '../../api/GetEditContact';
import Menubar from '../../components/menuBar/MenuBar';

const EditContact = () => {
    const { id }: any = useParams();
    const [ contactData, setContactData ] = useState({} as ContactDetailModelType)
    const [ isToggleMenu, setIsToggleMenu ] = useState(false);
    const [ editInput, setEditInput] = useState(
        {} as ContactDetailModelType
        );
    const history = useHistory();


    useEffect(()=>{
        getEditContact()
    },[id])

    const getEditContact = () => {
        GetEditContact(id).then((result) => {
            setContactData(result)
            console.log(result, 'apani')
        })
    }

    const toggleMenu = () => {
        setIsToggleMenu(!isToggleMenu);
    }

    const handleChange = (e:any) => {
        e.preventDefault();
        const { value,  name} = e.target;
        setEditInput((multipleInput) => ({
            ...multipleInput,
            [name]: value,
        }));
        console.log(value, 'apani2')
    } 

    return (
        <div className="contact-home">
            <Menubar
                isToggleMenu={isToggleMenu}
                toggleMenu={toggleMenu}
            />
            <div className="contact-main">
                <div className="header">
                    <span className="title">Edit Page</span>
                    <div className="line"/>
                </div>
                <div className="contact-edit">
                    <div className="input-field">
                        <span className="label">First Name</span>
                        <input 
                            type="text"
                            name="firstName"
                            onChange={handleChange}
                            value={editInput.firstName}
                        />
                    </div>
                    <div className="input-field">
                        <span className="label">Last Name</span>
                        <input 
                            type="text"
                            name="lastName"
                            onChange={handleChange}
                            value={editInput.lastName}
                        />
                    </div>
                </div>
            </div>
        </div>
    )
}

export default EditContact;
