import React, { useEffect, useState } from 'react'
import './Home.scss'
import axios from 'axios';
import { useDispatch, useSelector } from 'react-redux';
import { GetContact } from '../../api/GetContact';
import { ContactDetailModelType, ContactModelType } from '../../models/ContactModel';
import { withStyles, Theme, createStyles, makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import { defaultAvatar } from '../../helpers/defaultAvatar';
import { useParams } from 'react-router';
import { GetEditContact } from '../../api/GetEditContact';

const EditContact = () => {
    const { id }: any = useParams();
    const [ contactData, setContactData ] = useState([] as ContactDetailModelType[])
    const [ isToggleMenu, setIsToggleMenu ] = useState(false);


    useEffect(()=>{
        getEditContact()
    },[id])

    const getEditContact = () => {
        GetEditContact(id).then((result) => {
            setContactData(result)
            console.log(result, 'apani')
        })
    }

    const toggleMenu = () => {
        setIsToggleMenu(!isToggleMenu);
    }


    //Table Styling
    const StyledTableCell = withStyles((theme: Theme) =>
        createStyles({
            head: {
                backgroundColor: theme.palette.common.black,
                color: theme.palette.common.white,
            },
            body: {
                fontSize: 14,
            },
        }),
    )(TableCell);

    const StyledTableRow = withStyles((theme: Theme) =>
        createStyles({
            root: {
            '&:nth-of-type(odd)': {
                backgroundColor: theme.palette.action.hover,
            },
            },
        }),
    )(TableRow);

    const useStyles = makeStyles({
        table: {
          minWidth: 700,
        },
      });

      const classes = useStyles();
    return (
        <div className="contact-home">
            <div className={`menu-bar ${isToggleMenu ? 'show' : ''}`} >
                <div className="menu-header">
                    <div className="logo">
                        <img src="https://1.bp.blogspot.com/-KfCsyDWCa90/YHl1dr74s6I/AAAAAAAAEvE/Tc9vt0TdZjcm9161h0wGqt6Ns9QSH4kegCLcBGAsYHQ/s1600/Logo%2BDogecoin.png" style={{width: '120px'}}/>
                    </div>
                    <div className="toggle" onClick={toggleMenu}>
                        X
                    </div>
                </div>
                <div className="menu-item">
                    All Contact List
                </div>
                <div className="menu-item">
                    My Contact
                </div>
                <div className="menu-item">
                    About
                </div>
            </div>
            <div className="contact-main">
                <div className="header">
                    Selamat datang
                </div>
                <div className="contact-edit">
                    {contactData.map((res) => {
                        return (
                            <div>
                                {res.firstName}
                            </div>
                        )
                    })}
                </div>
            </div>
        </div>
    )
}

export default EditContact;
