import React, { useEffect, useState } from 'react'
import axios from 'axios';
import './EditContact.scss'
import Menubar from '../../components/menuBar/MenuBar';
import { useDispatch, useSelector } from 'react-redux';
import { GetContact } from '../../api/GetContact';
import { ContactDetailModelType } from '../../models/ContactModel';
import { defaultAvatar } from '../../helpers/defaultAvatar';
import { useHistory, useParams } from 'react-router';
import { GetEditContact } from '../../api/GetEditContact';

const EditContact = () => {
    const { id }: any = useParams();
    const [ isToggleMenu, setIsToggleMenu ] = useState(false);
    const [ editInput, setEditInput] = useState(
        {} as ContactDetailModelType
        );
    const history = useHistory();


    useEffect(()=>{
        getEditContact()
    },[id])

    const getEditContact = () => {
        GetEditContact(id).then((result) => {
            setEditInput(result)
            console.log(result, 'apani')
        })
    }

    const toggleMenu = () => {
        setIsToggleMenu(!isToggleMenu);
    }

    const handleChange = (e:any) => {
        e.preventDefault();
        const { value,  name} = e.target;
        setEditInput((multipleInput) => ({
            ...multipleInput,
            [name]: value,
        }));
        console.log(value, 'apani2')
    } 

    return (
        <div className="contact-home">
            <Menubar
                isToggleMenu={isToggleMenu}
                toggleMenu={toggleMenu}
            />
            <div className="contact-main">
                <div className="header">
                    <span className="title">Edit Page</span>
                    <div className="line"/>
                </div>
                <div className="contact-edit">
                    <div className='photo-container'>
                        <img src={defaultAvatar(editInput.photo)} alt='avatar' />
                        <label htmlFor='file-input' className='change-profile-photo'>
                        <span>Ganti foto profil</span>
                        <input
                            id='file-input'
                            name='file'
                            type='file'
                            onChange={changePhoto}
                        />
                        </label>
                    </div>
                    <div className="input-field">
                        <span className="label">First Name</span>
                        <input 
                            type="text"
                            name="firstName"
                            onChange={handleChange}
                            value={editInput.firstName}
                        />
                    </div>
                    <div className="input-field">
                        <span className="label">Last Name</span>
                        <input 
                            type="text"
                            name="lastName"
                            onChange={handleChange}
                            value={editInput.lastName}
                        />
                    </div>
                    <div className="input-field">
                        <span className="label">Age</span>
                        <input 
                            type="number"
                            name="age"
                            onChange={handleChange}
                            value={editInput.age}
                        />
                    </div>
                </div>
            </div>
        </div>
    )
}

export default EditContact;
